import React, { useEffect, useState } from 'react';

function Home() {
  const [videos, setVideos] = useState([]);

  const fetchVideos = async () => {
    try {
      const response = await fetch('http://localhost:3001/videos');
      if (!response.ok) {
        throw new Error(`Erro ao buscar os vídeos: ${response.status}`);
      }
      const data = await response.json();
      console.log('Vídeos carregados:', data);
      setVideos(data);
    } catch (error) {
      console.error('Erro ao carregar os vídeos:', error);
    }
  };

  useEffect(() => {
    fetchVideos();
  }, []);

  return (
    <div style={{ paddingTop: '70px', backgroundColor: '#111', minHeight: '100vh', color: '#fff' }}>
      <h1 style={{ textAlign: 'center', color: 'red', marginBottom: '20px' }}>Lista de Vídeos</h1>
      <div className="card-container">
        {videos.length > 0 ? (
          videos.map((video) => (
            <div className="card" key={video.id}>
              <iframe
                width="100%"
                height="200"
                src={`https://www.youtube.com/embed/${new URL(video.url).searchParams.get('v')}`}
                title={video.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>
              <h3>{video.title}</h3>
              <button>Assistir</button>
            </div>
          ))
        ) : (
          <p style={{ textAlign: 'center' }}>Nenhum vídeo disponível no momento.</p>
        )}
      </div>
    </div>
  );
}

export default Home;
